import React from 'react';
import { storesService } from '../../../services/stores.service';
import { ratingsService } from '../../../services/ratings.service';
import { useAuth } from '../../../contexts/AuthContext';
import RatingStars from '../../common/RatingStars/RatingStars';
import RatingForm from '../../ratings/RatingForm/RatingForm';
import './StoreList.css';

interface StoreListProps {
  nameFilter?: string;
  addressFilter?: string;
  minRating?: number;
  maxRating?: number;
}

const StoreList: React.FC<StoreListProps> = ({ 
  nameFilter = '', 
  addressFilter = '',
  minRating,
  maxRating
}) => {
  const [stores, setStores] = useState<any[]>([]);
  const [total, setTotal] = useState(0);
  const [page, setPage] = useState(1);
  const [limit] = useState(10);
  const [sortBy, setSortBy] = useState('name');
  const [sortOrder, setSortOrder] = useState<'ASC' | 'DESC'>('ASC');
  const [selectedStore, setSelectedStore] = useState<any>(null);
  const [userRatings, setUserRatings] = useState<Record<number, any>>({});
  const [loading, setLoading] = useState(true);
  
  const { user } = useAuth();
  const isUser = user?.role === 'user';

  useEffect(() => {
    loadStores();
    if (isUser) {
      loadUserRatings();
    }
  }, [page, sortBy, sortOrder, nameFilter, addressFilter, minRating, maxRating]);

  const loadStores = async () => {
    try {
      const filters: any = {};
      if (nameFilter) filters.name = nameFilter;
      if (addressFilter) filters.address = addressFilter;
      if (minRating !== undefined) filters.minRating = minRating;
      if (maxRating !== undefined) filters.maxRating = maxRating;

      const response = await storesService.getStores(
        filters, 
        page, 
        limit, 
        sortBy, 
        sortOrder
      );
      
      setStores(response.stores);
      setTotal(response.total);
    } catch (error) {
      console.error('Failed to load stores:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadUserRatings = async () => {
    try {
      const ratings: Record<number, any> = {};
      for (const store of stores) {
        const rating = await ratingsService.getUserRatingForStore(store.id);
        if (rating) {
          ratings[store.id] = rating;
        }
      }
      setUserRatings(ratings);
    } catch (error) {
      console.error('Failed to load user ratings:', error);
    }
  };

  const handleSort = (column: string) => {
    if (sortBy === column) {
      setSortOrder(sortOrder === 'ASC' ? 'DESC' : 'ASC');
    } else {
      setSortBy(column);
      setSortOrder('ASC');
    }
  };

  const handleRatingSubmit = async () => {
    setSelectedStore(null);
    await loadStores();
    if (isUser) {
      await loadUserRatings();
    }
  };

  const totalPages = Math.ceil(total / limit);

  if (loading) {
    return <div>Loading stores...</div>;
  }

  return (
    <div className="store-list">
      {selectedStore && (
        <RatingForm 
          store={selectedStore} 
          onClose={() => setSelectedStore(null)}
          onSubmit={handleRatingSubmit}
          existingRating={userRatings[selectedStore.id]}
        />
      )}
      
      <div className="table-container">
        <table className="table">
          <thead>
            <tr>
              <th onClick={() => handleSort('name')}>
                Name {sortBy === 'name' && (sortOrder === 'ASC' ? 'â†‘' : 'â†“')}
              </th>
              <th onClick={() => handleSort('address')}>
                Address {sortBy === 'address' && (sortOrder === 'ASC' ? 'â†‘' : 'â†“')}
              </th>
              <th onClick={() => handleSort('avgRating')}>
                Rating {sortBy === 'avgRating' && (sortOrder === 'ASC' ? 'â†‘' : 'â†“')}
              </th>
              {isUser && <th>Your Rating</th>}
              {isUser && <th>Actions</th>}
            </tr>
          </thead>
          <tbody>
            {stores.map((store) => (
              <tr key={store.id}>
                <td>{store.name}</td>
                <td>{store.address}</td>
                <td>
                  <RatingStars rating={store.avgRating || 0} />
                  <span className="rating-count">({store.ratings?.length || 0})</span>
                </td>
                {isUser && (
                  <td>
                    {userRatings[store.id] ? (
                      <RatingStars rating={userRatings[store.id].rating} />
                    ) : (
                      'Not rated yet'
                    )}
                  </td>
                )}
                {isUser && (
                  <td>
                    <button 
                      className="btn-primary"
                      onClick={() => setSelectedStore(store)}
                    >
                      {userRatings[store.id] ? 'Update Rating' : 'Rate Store'}
                    </button>
                  </td>
                )}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      
      {totalPages > 1 && (
        <div className="pagination">
          <button 
            disabled={page === 1}
            onClick={() => setPage(page - 1)}
          >
            Previous
          </button>
          
          {Array.from({ length: totalPages }, (_, i) => i + 1).map((pageNum) => (
            <button
              key={pageNum}
              className={page === pageNum ? 'active' : ''}
              onClick={() => setPage(pageNum)}
            >
              {pageNum}
            </button>
          ))}
          
          <button 
            disabled={page === totalPages}
            onClick={() => setPage(page + 1)}
          >
            Next
          </button>
        </div>
      )}
    </div>
  );
};

export default StoreList;
