import React from 'react';
import { usersService } from '../../../services/users.service';
import { storesService } from '../../../services/stores.service';
import UserList from '../../users/UserList/UserList';
import StoreList from '../../stores/StoreList/StoreList';
import './AdminDashboard.css';

const AdminDashboard: React.FC = () => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [stats, setStats] = useState({
    totalUsers: 0,
    totalStores: 0,
    totalRatings: 0,
    totalAdmins: 0,
    totalStoreOwners: 0,
    totalNormalUsers: 0,
  });

  useEffect(() => {
    loadStats();
  }, []);

  const loadStats = async () => {
    try {
      const [userStats, storeStats] = await Promise.all([
        usersService.getUserStats(),
        storesService.getStoreStats(),
      ]);
      
      setStats({
        totalUsers: userStats.totalUsers,
        totalAdmins: userStats.totalAdmins,
        totalStoreOwners: userStats.totalStoreOwners,
        totalNormalUsers: userStats.totalNormalUsers,
        totalStores: storeStats.totalStores,
        totalRatings: storeStats.totalRatings,
      });
    } catch (error) {
      console.error('Failed to load stats:', error);
    }
  };

  return (
    <div className="dashboard">
      <h1>Admin Dashboard</h1>
      
      <div className="tabs">
        <button 
          className={activeTab === 'dashboard' ? 'tab active' : 'tab'}
          onClick={() => setActiveTab('dashboard')}
        >
          Dashboard
        </button>
        <button 
          className={activeTab === 'users' ? 'tab active' : 'tab'}
          onClick={() => setActiveTab('users')}
        >
          Users
        </button>
        <button 
          className={activeTab === 'stores' ? 'tab active' : 'tab'}
          onClick={() => setActiveTab('stores')}
        >
          Stores
        </button>
      </div>
      
      <div className="tab-content">
        {activeTab === 'dashboard' && (
          <div className="stats-grid">
            <div className="stat-card">
              <h3>Total Users</h3>
              <p className="stat-number">{stats.totalUsers}</p>
            </div>
            <div className="stat-card">
              <h3>Total Stores</h3>
              <p className="stat-number">{stats.totalStores}</p>
            </div>
            <div className="stat-card">
              <h3>Total Ratings</h3>
              <p className="stat-number">{stats.totalRatings}</p>
            </div>
            <div className="stat-card">
              <h3>Admins</h3>
              <p className="stat-number">{stats.totalAdmins}</p>
            </div>
            <div className="stat-card">
              <h3>Store Owners</h3>
              <p className="stat-number">{stats.totalStoreOwners}</p>
            </div>
            <div className="stat-card">
              <h3>Normal Users</h3>
              <p className="stat-number">{stats.totalNormalUsers}</p>
            </div>
          </div>
        )}
        
        {activeTab === 'users' && <UserList />}
        {activeTab === 'stores' && <StoreList />}
      </div>
    </div>
  );
};

export default AdminDashboard;
